import React, { useEffect, useState } from "react";
import personService from './services'
import { nanoid } from 'nanoid'
const Notification =({ message }) => {
  if (message === null) {
    return null
  }
  return (
    <div className='notice'>
      {message}
    </div>
  )
}
const PersonForm = (props) => {
    const [persons, setPersons] = props.person
    const [noticeMessage, setNoticeMessage] = props.person
    const [newName, setNewName] = useState('')
    const [newNumber, setNewNumber] = useState('')

    const addName = (event) => {
        event.preventDefault()
        const nameObj = {
          name: newName,
          number: newNumber,
          id: nanoid()
        }
        if (persons.find((person)=> person.name === newName)){
          const id = persons.find(person=>person.name===newName).id
          const changedPerson = {...nameObj, number: nameObj.number}
          if (window.confirm(`${newName} is already added to phonebook, replace the old number with a new one?`)) {
            personService
            .update(id, changedPerson)
            .then(returnPerson=>{
              setPersons(persons.map(person => person.id !== id ? person : returnPerson))
              setNewName('')
              setNewNumber('')
            })
          }
          return
        }else {
          personService
          .create(nameObj)
          .then(returnPerson=>{
            setTimeout(()=>{
              setNoticeMessage(`Added ${returnPerson.name}`)
            }, 5000)
            setPersons(persons.concat(returnPerson))
            setNewName('')
            setNewNumber('')
          })
        }

    }

    const handleNameChange = (event) => {
        const{value} = event.target
        //console.log('value', value)

        setNewName(value)

    }

    const handleNumberChange = (event) => {
        setNewNumber(event.target.value)

    }

    return (
      <div>
        <Notification message={noticeMessage}/>
        <form onSubmit={addName}>
        <div>
          name: <input value={newName} onChange={handleNameChange}/>
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange} />
        </div>
        <div>
          <button type="submit" >add</button>
        </div>
        </form>
      </div>

    );
}

export default PersonForm;
