import React, { useEffect, useState } from "react";
import personService from './services'
import { nanoid } from 'nanoid'
const PersonForm = (props) => {
    //const {person, replaceNumber} = props
    const [persons, setPersons] = props.person

    const [newName, setNewName] = useState('')
    const [newNumber, setNewNumber] = useState('')
    const addName = (event) => {


        event.preventDefault()
        //const id = nanoid()


        const nameObj = {
          name: newName,
          number: newNumber,
          id: nanoid()

        }
        //console.log('nameobj', nameObj)
        personService
        .create(nameObj)
        .then(returnPerson=>{
          setPersons(persons.concat(returnPerson))
          //console.log('addpersons', persons)
          setNewName('')
          setNewNumber('')
        })
    }
    const handleNameChange = (event) => {
        const{value} = event.target
        //console.log('value', value)

        setNewName(value)
        if (persons.find(({name})=> name === value)) {

          return window.alert(`${value} is already added to phonebook`)
        }
    }

    const handleNumberChange = (event) => {
        setNewNumber(event.target.value)

    }


    // const replaceNumber = (nameObj) => {
    //   console.log('nameoBj', nameObj)

    //   if (window.confirm(`${nameObj.name} is already added`)) {

    //     personService
    //     .update(nameObj.id, nameObj)
    //     .then(returnedPersons=>{
    //       setPersons(returnedPersons)
    //     })
    //   }
    // }


    return (
        <form onSubmit={addName}>
        <div>
          name: <input value={newName} onChange={handleNameChange}/>
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange} />
        </div>
        <div>
          <button type="submit" >add</button>
        </div>
      </form>
    );
}

export default PersonForm;
