import { useState, useEffect } from 'react'
import axios from 'axios'
import Persons from './components/Persons'
import Filter from './components/Filter'
import PersonForm from './components/PersonForm'
import personService from './components/services'


const App = () => {
  const [persons, setPersons] = useState([]) 

  //console.log('persons', persons)

  useEffect(() => {
    console.log('effect')
    personService
    .getAll()
    .then(allpersons =>{
      setPersons(allpersons)
    })
  }, [])

  const deletePerson =person=>{
    console.log('beforedelele', persons)
    if (window.confirm(`Delete ${person.name} ${person.number}`)) {
    //const newpersons = persons.filter((item)=>item !== person)

    personService
    .deletePerson(person.id)
    .then(() =>{
      setPersons(persons.filter(newperson=>newperson.id != person.id))


    })
    //console.log('afterdelete', persons)
    }

  }

  const replaceNumber = (personNumber, personName) => {
    console.log('input', personName, personNumber)
    const person = persons.find(person=>person.name === personName)
    const id = person.id 
    const newPerson = {...person, number:personNumber}

    if (window.confirm(`${personName} is already added`)) {

      personService
      .update(id, newPerson)
      .then(returnedPersons=>{
        setPersons(returnedPersons)
      })
    }


    }


  return (

    <div>
      <h2>Phonebook</h2>
      <Filter person={[persons, setPersons]}/>

      <h3>Add a new</h3>
      <PersonForm person={[persons, setPersons]} replaceNumber={replaceNumber}/>
      <h3>Numbers</h3>

      <Persons persons={persons} deletePerson= {deletePerson}/>

    </div>
  )
}

export default App
