import { useState } from 'react'
import Person from './components/Person'
const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ]) 
  //console.log('persons', persons)
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  //const [newAdded, setNewAdded] = useState('')
  //const [nameNumber, setNameNumber] = useState('')
  const addName = (event) => {
    event.preventDefault()
    const nameObj = {
      name: newName,
      number: newNumber,
      id: persons.length+1

    }
    setPersons(persons.concat(nameObj))
    setNewName('')
    setNewNumber('')
  }

  const handleNameChange = (event) => {
    const{value} = event.target
    //console.log('value', value)
    if (persons.find(({name})=> name === value)) {
      return window.alert(`${value} is already added to phonebook`)
    }
    return setNewName(value)
  }
  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)

  }
  console.log('persons', persons)
  const filterNameNumber = (event, persons) => {
    //console.log('persons', persons)
    const new_persons = persons.filter(person=> person.name === event.target.value)
    console.log('new_persons', new_persons)
    setPersons(new_persons)
  }

  return (

    <div>
      <h2>Phonebook</h2>
      <div>filter shown with<input  onChange={filterNameNumber}/></div>
      <h2>add a new</h2>
      <form onSubmit={addName}>
        <div>
          name: <input value={newName} onChange={handleNameChange}/>
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>

      {
        persons.map((person)=> <Person key={person.id} person={person}/>)
      }

    </div>
  )
}

export default App
